mapper.select:
SELECT

FROM command_task


        AND command_name LIKE CONCAT('%', #{commandName}, '%')


        AND status = #{status}


        AND creator = #{creator}


        AND task_type = #{taskType}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


ORDER BY create_time DESC

SELECT COUNT(*)
FROM command_task


        AND command_name LIKE CONCAT('%', #{commandName}, '%')


        AND status = #{status}


        AND creator = #{creator}


        AND task_type = #{taskType}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}

mapper.update:
UPDATE command_task
command_name = #{commandName}, command_content = #{commandContent}, status = #{status}, executed_count = #{executedCount}, total_count = #{totalCount}, deploy_time = #{deployTime}, update_time = #{updateTime}, target_tags = #{targetTags}, target_hosts = #{targetHosts}, task_type = #{taskType}, scheduled_time = #{scheduledTime}, timeout_seconds = #{timeoutSeconds}, creator = #{creator}, description = #{description}, result_summary = #{resultSummary},
WHERE id = #{id}
