mapper.delete:
DELETE FROM container_info WHERE HOST_ID = #{hostId}

    AND NAMES NOT IN

        #{item}

mapper.insert:
INSERT INTO container_info (ID, HOST_ID, HOST_NAME, CONTAINER_ID, NAMES, IMAGE, STATE, CPU_PER, MEM_PER, MEM_CACHE, UPTIME, ALERT_STATE, LAST_HEARTBEAT) VALUES
(#{item.id}, #{item.hostId}, #{item.hostName}, #{item.containerId}, #{item.names}, #{item.image}, #{item.state}, #{item.cpuPer}, #{item.memPer}, #{item.memCache}, #{item.uptime}, '0', #{item.lastHeartbeat})
ON DUPLICATE KEY UPDATE CONTAINER_ID = VALUES(CONTAINER_ID), IMAGE = VALUES(IMAGE), STATE = VALUES(STATE), CPU_PER = VALUES(CPU_PER), MEM_PER = VALUES(MEM_PER), MEM_CACHE = VALUES(MEM_CACHE), UPTIME = VALUES(UPTIME), ALERT_STATE = '0', LAST_HEARTBEAT = VALUES(LAST_HEARTBEAT);

mapper.update:
UPDATE container_info
HOST_ID = #{hostId}, HOST_NAME = #{hostName}, CONTAINER_ID = #{containerId}, NAMES = #{names}, IMAGE = #{image}, STATE = #{state}, CPU_PER = #{cpuPer}, MEM_PER = #{memPer}, MEM_CACHE = #{memCache}, UPTIME = #{uptime}, ALERT_STATE = #{alertState}, LAST_HEARTBEAT = #{lastHeartbeat}
WHERE ID = #{id}

mapper.select:
SELECT

FROM container_info
WHERE ALERT_STATE = '0' AND LAST_HEARTBEAT <= #{lastHeartbeat}
